import{_ as e,o,c as n,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 33.2-333 Emergency paving of unpaved secondary highways; notice and local concurrence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-333.md","filePath":"vacode/33.2-333.md"}'),i={name:"vacode/33.2-333.md"},t=a('<h1 id="§-33-2-333-emergency-paving-of-unpaved-secondary-highways-notice-and-local-concurrence" tabindex="-1">§ 33.2-333 Emergency paving of unpaved secondary highways; notice and local concurrence. <a class="header-anchor" href="#§-33-2-333-emergency-paving-of-unpaved-secondary-highways-notice-and-local-concurrence" aria-label="Permalink to &quot;§ 33.2-333 Emergency paving of unpaved secondary highways; notice and local concurrence.&quot;">​</a></h1><p>In the event of an emergency, an unpaved highway within the secondary state highway system shall be paved only if the following procedures are satisfied:</p><ol><li><p>The Commissioner of Highways shall consider the following factors in determining whether the unpaved secondary highway, as the result of an emergency, shall be paved: (i) the safety of the secondary highway in its current condition; (ii) the feasibility of restoring the unpaved highway to its functional level prior to the emergency; (iii) the concerns of the citizens in the locality wherein the affected highway is located, particularly those persons who own land adjacent to such highway; (iv) the concerns of the governing body of the locality affected; and (v) the historical and aesthetic significance of the unpaved secondary highway and its surroundings.</p></li><li><p>The Commissioner of Highways shall provide notice of the intended paving to the governing body of the locality where the affected highway or portion thereof is located. The Commissioner shall provide such notice following his decision to pave the unpaved secondary highway within the locality affected.</p></li><li><p>The local governing body&#39;s concurrence or other recommendation regarding the proposed paving shall be forwarded to the Commissioner of Highways within 72 hours following the receipt of the Commissioner&#39;s notice.</p></li></ol><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0923" target="_blank" rel="noreferrer">923</a>, § 33.1-70.2; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0400" target="_blank" rel="noreferrer">400</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),r=[t];function c(h,s,l,d,g,f){return o(),n("div",null,r)}const v=e(i,[["render",c]]);export{y as __pageData,v as default};
