import{_ as t,o as e,c as o,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-337 Contributions to primary or secondary state highway construction by counties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-337.md","filePath":"vacode/33.2-337.md"}'),a={name:"vacode/33.2-337.md"},n=r('<h1 id="§-33-2-337-contributions-to-primary-or-secondary-state-highway-construction-by-counties" tabindex="-1">§ 33.2-337 Contributions to primary or secondary state highway construction by counties. <a class="header-anchor" href="#§-33-2-337-contributions-to-primary-or-secondary-state-highway-construction-by-counties" aria-label="Permalink to &quot;§ 33.2-337 Contributions to primary or secondary state highway construction by counties.&quot;">​</a></h1><p>Notwithstanding any other provision of law, any county having highways in the primary or secondary state highway system may contribute funds annually for the construction of primary or secondary highways. The funds contributed by such county shall be appropriated from the county&#39;s general revenues for use by the Department on the primary or secondary state highway system within such county as may be determined by the board of supervisors of such county in cooperation with the Department. The funds to which any county may be entitled under the provisions of § <a href="http://law.lis.virginia.gov/vacode/33.2-358/" target="_blank" rel="noreferrer">33.2-358</a> for construction, improvement, or maintenance of primary or secondary highways shall not be diminished by reason of any funds contributed for that purpose by such county or by any person or entity, regardless of whether such contributions are matched by state or federal funds.</p><p>1977, c. 578, § 33.1-75.2; 1982, c. 218; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>.</p>',3),i=[n];function s(c,h,y,u,d,p){return e(),o("div",null,i)}const b=t(a,[["render",s]]);export{f as __pageData,b as default};
