import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 33.2-340 Maintenance, etc., by Commissioner of Highways when no request for allocation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-340.md","filePath":"vacode/33.2-340.md"}'),n={name:"vacode/33.2-340.md"},r=o('<h1 id="§-33-2-340-maintenance-etc-by-commissioner-of-highways-when-no-request-for-allocation" tabindex="-1">§ 33.2-340 Maintenance, etc., by Commissioner of Highways when no request for allocation. <a class="header-anchor" href="#§-33-2-340-maintenance-etc-by-commissioner-of-highways-when-no-request-for-allocation" aria-label="Permalink to &quot;§ 33.2-340 Maintenance, etc., by Commissioner of Highways when no request for allocation.&quot;">​</a></h1><p>If no request is made to the Board by the governing body of any town as provided in § <a href="http://law.lis.virginia.gov/vacode/33.2-339/" target="_blank" rel="noreferrer">33.2-339</a>, the Commissioner of Highways, subject to the approval of the Board, may maintain, improve, construct, and reconstruct all streets in such town that (i) have an unrestricted right-of-way width of not less than 30 feet and a hard-surface width of not less than 12 feet; (ii) were established after July 1, 1950, by such town and have a right-of-way width of not less than 50 feet and a hard-surface width of not less than 20 feet; or (iii) are functionally classified as local streets and were constructed on or after January 1, 1996, and, at the time of approval by the town, met the criteria for pavement width and right-of-way of the then-current edition design standards for subdivision streets as set forth in regulations adopted by the Board.</p><p>Code 1950; 1950, p. 162, § 33-50.4; 1970, c. 322, § 33.1-82; 1992, c. 108; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[r];function s(c,h,d,f,l,_){return t(),a("div",null,i)}const p=e(n,[["render",s]]);export{u as __pageData,p as default};
