import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-3402 NVTC oversight.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-3402.md","filePath":"vacode/33.2-3402.md"}'),i={name:"vacode/33.2-3402.md"},r=n('<h1 id="§-33-2-3402-nvtc-oversight" tabindex="-1">§ 33.2-3402 NVTC oversight. <a class="header-anchor" href="#§-33-2-3402-nvtc-oversight" aria-label="Permalink to &quot;§ 33.2-3402 NVTC oversight.&quot;">​</a></h1><p>A. In any year that funds are deposited into the Fund, the NVTC shall request certain documents and reports from WMATA to confirm the benefits of the WMATA system to persons living, traveling, commuting, and working in the localities that the NVTC comprises. Such documents and reports shall include:</p><ol><li><p>WMATA&#39;s annual capital budget;</p></li><li><p>WMATA&#39;s annual independent financial audit;</p></li><li><p>WMATA&#39;s National Transit Data annual profile; and</p></li><li><p>Single audit reports issued in accordance with the Uniform Administrative Requirements, Cost Principals, and Audit Requirements for Federal Awards (2 C.F.R. Part 200).</p></li></ol><p>B. NVTC shall be responsible for coordinating the delivery of such documents and reports with WMATA. Funding of the Commonwealth to support WMATA pursuant to § 33.2-1526.1 shall be contingent on WMATA providing the documents and reports described in subsection A, and NVTC shall provide annual certification to the Comptroller that such documents and reports have been received.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>.</p>',5),o=[r];function s(l,c,d,p,h,u){return t(),a("div",null,o)}const m=e(i,[["render",s]]);export{f as __pageData,m as default};
