import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 33.2-347 Minimum street and highway standards for certain towns.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-347.md","filePath":"vacode/33.2-347.md"}'),n={name:"vacode/33.2-347.md"},o=r('<h1 id="§-33-2-347-minimum-street-and-highway-standards-for-certain-towns" tabindex="-1">§ 33.2-347 Minimum street and highway standards for certain towns. <a class="header-anchor" href="#§-33-2-347-minimum-street-and-highway-standards-for-certain-towns" aria-label="Permalink to &quot;§ 33.2-347 Minimum street and highway standards for certain towns.&quot;">​</a></h1><p>Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/33.2-340/" target="_blank" rel="noreferrer">33.2-340</a>, any town in which 70 percent or more of developable land within its boundaries has a natural grade of 20 percent or more may by ordinance provide for streets or highways established on or after July 1, 1980, with an unrestricted right-of-way width of not less than 40 feet and a hard-surface width of not less than 18 feet, provided that no such requirement of any such town shall be less stringent than that of the county in which the town is located. Streets and highways so established and constructed shall be eligible for payment in accordance with § <a href="http://law.lis.virginia.gov/vacode/33.2-340/" target="_blank" rel="noreferrer">33.2-340</a>.</p><p>1980, c. 374, § 33.1-43.2; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[o];function s(h,d,c,l,f,_){return t(),a("div",null,i)}const w=e(n,[["render",s]]);export{p as __pageData,w as default};
