import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-352 Asset management practices; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-352.md","filePath":"vacode/33.2-352.md"}'),r={name:"vacode/33.2-352.md"},i=n('<h1 id="§-33-2-352-asset-management-practices-report" tabindex="-1">§ 33.2-352 Asset management practices; report. <a class="header-anchor" href="#§-33-2-352-asset-management-practices-report" aria-label="Permalink to &quot;§ 33.2-352 Asset management practices; report.&quot;">​</a></h1><p>A. The Department shall develop asset management practices in the operation and maintenance of the systems of state highways. Such practices shall include a transparent methodology for the allocation of funds from the Highway Maintenance and Operating Fund established pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-1530/" target="_blank" rel="noreferrer">33.2-1530</a> to highway systems maintenance and operations programs, including the allocations among the highway construction districts and among the Interstate System and primary and secondary state highway systems.</p><p>B. The Commissioner of Highways shall advise the Board on or before June 30 of even-numbered years of performance targets and outcomes that are expected to be achieved, based on the funding identified for maintenance, over the biennium beginning July 1 of that year. In addition, not later than September 30 of even-numbered years, the Commissioner of Highways shall advise the Board on the Department&#39;s accomplishments relative to the expected outcomes and budget expenditures for the biennium ending June 30 of that year and also advise the Board as to the methodology used to determine maintenance needs and the justification as to the maintenance funding by source.</p><p>1985, c. 42, § 33.1-23.02; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0302" target="_blank" rel="noreferrer">302</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0570" target="_blank" rel="noreferrer">570</a>; 2006, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?062+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>.</p>',4),o=[i];function s(c,h,l,d,p,g){return a(),t("div",null,o)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
