import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-354 Commonwealth Transportation Board to develop and update Statewide Pedestrian Policy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-354.md","filePath":"vacode/33.2-354.md"}'),n={name:"vacode/33.2-354.md"},i=o('<h1 id="§-33-2-354-commonwealth-transportation-board-to-develop-and-update-statewide-pedestrian-policy" tabindex="-1">§ 33.2-354 Commonwealth Transportation Board to develop and update Statewide Pedestrian Policy. <a class="header-anchor" href="#§-33-2-354-commonwealth-transportation-board-to-develop-and-update-statewide-pedestrian-policy" aria-label="Permalink to &quot;§ 33.2-354 Commonwealth Transportation Board to develop and update Statewide Pedestrian Policy.&quot;">​</a></h1><p>A. The Board shall develop and update as needed a Statewide Pedestrian Policy. The Board shall:</p><ol><li><p>Provide opportunities for receipt of comments, suggestions, and information from local governments, business and civic organizations, and other concerned parties;</p></li><li><p>Identify and evaluate needs at statewide, regional, and local levels for additional facilities required to promote pedestrian access to schools, places of employment and recreation, and major activity centers;</p></li><li><p>Consider and evaluate potential ways of meeting these needs; and</p></li><li><p>Set forth conclusions as to goals, objectives, and strategies to meet these needs in a safety-conscious manner.</p></li></ol><p>B. The Board shall coordinate the development of the Statewide Pedestrian Policy with that of the Statewide Transportation Plan provided for in § <a href="http://law.lis.virginia.gov/vacode/33.2-353/" target="_blank" rel="noreferrer">33.2-353</a> and cover the same 20-year planning horizon. The Statewide Pedestrian Policy shall be summarized in a public document and made available to the general public upon presentation to the Governor and General Assembly, either in combination with the Statewide Transportation Plan or as a separate document.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, § 33.1-23.03:001; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',5),r=[i];function d(s,l,p,c,h,m){return a(),t("div",null,r)}const v=e(n,[["render",d]]);export{f as __pageData,v as default};
