import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-359 Unpaved secondary highway funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-359.md","filePath":"vacode/33.2-359.md"}'),o={name:"vacode/33.2-359.md"},n=r('<h1 id="§-33-2-359-unpaved-secondary-highway-funds" tabindex="-1">§ 33.2-359 Unpaved secondary highway funds. <a class="header-anchor" href="#§-33-2-359-unpaved-secondary-highway-funds" aria-label="Permalink to &quot;§ 33.2-359 Unpaved secondary highway funds.&quot;">​</a></h1><p>A. Funds from the highway construction district grant programs established pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-371/" target="_blank" rel="noreferrer">33.2-371</a> shall be allocated for the improvement of nonsurface treated secondary highways that carry 50 or more vehicles per day. Funds shall be deducted from the allocation made to each highway construction district pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/33.2-371/" target="_blank" rel="noreferrer">33.2-371</a> and such deduction shall be based on the ratio of nonsurface treated secondary highways in each highway construction district that carry 50 or more vehicles per day to the total number of such nonsurface treated secondary highways in the Commonwealth.</p><p>Total funds of the Commonwealth allocated by the Board under this section shall not exceed $25 million annually.</p><p>B. Such funds shall be distributed to counties in the secondary state highway system based on the ratio of nonsurface treated roads in each county carrying 50 vehicles or more per day to the total number of such nonsurface treated roads in the Commonwealth.</p><p>1979, c. 84, § 33.1-23.1:1; 1985, c. 42; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>.</p>',5),s=[n];function i(c,h,d,l,u,f){return a(),t("div",null,s)}const g=e(o,[["render",i]]);export{_ as __pageData,g as default};
