import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-3602 Interstate 81 Corridor Improvement Program; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-3602.md","filePath":"vacode/33.2-3602.md"}'),o={name:"vacode/33.2-3602.md"},n=a('<h1 id="§-33-2-3602-interstate-81-corridor-improvement-program-report" tabindex="-1">§ 33.2-3602 Interstate 81 Corridor Improvement Program; report. <a class="header-anchor" href="#§-33-2-3602-interstate-81-corridor-improvement-program-report" aria-label="Permalink to &quot;§ 33.2-3602 Interstate 81 Corridor Improvement Program; report.&quot;">​</a></h1><p>A. The Board shall adopt an Interstate 81 Corridor Improvement Program. The Program shall, at a minimum:</p><ol><li><p>Allocate year by year the revenues, if any, from the Fund and bond proceeds, if any, backed by the Fund to projects and strategies identified in the Plan adopted by the Board;</p></li><li><p>Include a financing plan to support such allocation; and</p></li><li><p>Include a schedule for all new projects and strategies identified in the Plan adopted by the Board.</p></li></ol><p>B. Prior to the adoption of such Program, the Board shall review the recommendations of and consult with the Committee.</p><p>C. The Board shall update the Program annually by July 1.</p><p>D. By December 15 of each year, the Board shall report to the General Assembly regarding the status and progress of implementation of the Program. The report shall be submitted to the Chairmen of the House Committees on Appropriations and Transportation and the Senate Committees on Finance and Appropriations and on Transportation. Prior to the submission of such report each year, the Board shall consult with the Committee. The report shall include, at a minimum:</p><ol><li><p>The safety and performance of the Interstate 81 corridor, including the number of incidents, the average duration of incidents, the number and average duration of incidents involving lane closures, and the person-hours of delay along the Interstate 81 corridor;</p></li><li><p>An assessment of the effectiveness of the operational strategies and capital projects implemented and funded through the Program;</p></li><li><p>The status of capital projects funded through the Program; and</p></li><li><p>The current and projected balances of the Fund.</p></li></ol><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0837" target="_blank" rel="noreferrer">837</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>.</p>',8),i=[n];function s(l,d,p,h,c,m){return t(),r("div",null,i)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
