import{_ as o,o as i,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-3709 Additional Powers of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-3709.md","filePath":"vacode/33.2-3709.md"}'),r={name:"vacode/33.2-3709.md"},n=e("h1",{id:"§-33-2-3709-additional-powers-of-the-authority",tabindex:"-1"},[t("§ 33.2-3709 Additional Powers of the Authority. "),e("a",{class:"header-anchor",href:"#§-33-2-3709-additional-powers-of-the-authority","aria-label":'Permalink to "§ 33.2-3709 Additional Powers of the Authority."'},"​")],-1),l=e("p",null,"Notwithstanding any contrary provision of this title and in accordance with all applicable federal statutes and requirements, the Authority shall control and operate and may impose and collect tolls in amounts established by the Authority for the use of any new or improved highway, bridge, or tunnel, to increase capacity on such facility or to address congestion within Planning District 15, constructed by the Commission (i) with federal, state, or local funds, (ii) solely with revenues of the Authority, or (iii) with revenues under the control of the Authority. The amount of any such toll may be varied from facility to facility, by lane, by congestion levels, by day of the week, by time of day, by type or size of vehicle, by number of axles, or by any similar combination thereof or any other factor the Authority may deem proper, and a reduced rate may be established for commuters as defined by the Authority. All such tolls shall be used for programs and projects that are reasonably related to or benefit the users of the new or improved highway, bridge, or tunnel, including, but not limited to, for the debt service and other costs of bonds whose proceeds are used for construction or improvement of such highway, bridge, or tunnel.",-1),s=e("p",null,"Any tolls imposed by the Authority shall be collected by an electronic toll system that, to the extent possible, shall not impede the traffic flow of the facility or prohibit a toll facility from retaining means of nonautomated toll collection in some lanes of the facility. For all facilities tolled by the Authority, there shall be signs erected prior to the point of toll collection that clearly state how the majority of the toll revenue is being spent by the Authority to benefit the users of the facility.",-1),h=e("p",null,[t("2020, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1235",target:"_blank",rel:"noreferrer"},"1235"),t(".")],-1),c=[n,l,s,h];function d(f,y,u,m,b,p){return i(),a("div",null,c)}const w=o(r,[["render",d]]);export{g as __pageData,w as default};
