import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-374 Special Structure Program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-374.md","filePath":"vacode/33.2-374.md"}'),n={name:"vacode/33.2-374.md"},o=r('<h1 id="§-33-2-374-special-structure-program" tabindex="-1">§ 33.2-374 Special Structure Program. <a class="header-anchor" href="#§-33-2-374-special-structure-program" aria-label="Permalink to &quot;§ 33.2-374 Special Structure Program.&quot;">​</a></h1><p>A. For purposes of this section, &quot;special structure&quot; means very large, indispensable, and unique bridges and tunnels identified by the Commissioner and approved by the Commonwealth Transportation Board.</p><p>B. The General Assembly declares it to be in the public interest that the maintenance, rehabilitation, and replacement of special structures in the Commonwealth occur timely as to provide and protect a safe and efficient highway system.</p><p>C. The Board shall establish a program for the maintenance, rehabilitation, and replacement of special structures in the Commonwealth. With the assistance of the Department of Transportation, the Board shall develop and maintain a plan for the maintenance, rehabilitation, and replacement of special structures in the Commonwealth. The plan shall cover at a minimum a 30-year period and shall be updated biennially no later than November 1 of each even-numbered year.</p><p>D. The Board shall use the funds allocated in §§ <a href="/vacode/33.2-1524/">33.2-1524</a> and <a href="/vacode/33.2-1530/">33.2-1530</a> to the Special Structure Fund pursuant to § <a href="/vacode/33.2-1532/">33.2-1532</a> for maintenance, rehabilitation, and replacement of special structures to implement the plan developed pursuant to subsection C.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',6),i=[o];function s(l,c,p,h,d,u){return a(),t("div",null,i)}const _=e(n,[["render",s]]);export{f as __pageData,_ as default};
