import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 33.2-502 Designation of HOT lanes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-502.md","filePath":"vacode/33.2-502.md"}'),n={name:"vacode/33.2-502.md"},o=r('<h1 id="§-33-2-502-designation-of-hot-lanes" tabindex="-1">§ 33.2-502 Designation of HOT lanes. <a class="header-anchor" href="#§-33-2-502-designation-of-hot-lanes" aria-label="Permalink to &quot;§ 33.2-502 Designation of HOT lanes.&quot;">​</a></h1><p>The Board may designate one or more lanes of any highway, including lanes that may previously have been designated HOV lanes under § <a href="http://law.lis.virginia.gov/vacode/33.2-501/" target="_blank" rel="noreferrer">33.2-501</a>, in the Interstate System, primary state highway system, or National Highway System, or any portion thereof, as HOT lanes. In making HOT lanes designations, the Board shall also specify the high-occupancy requirement and conditions for use of such HOT lanes or may authorize the Commissioner of Highways to make such determination consistent with the terms of a comprehensive agreement executed pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-1808/" target="_blank" rel="noreferrer">33.2-1808</a>. The high-occupancy requirement for a HOT lanes facility constructed or operated as a result of the Public-Private Transportation Act (§ <a href="http://law.lis.virginia.gov/vacode/33.2-1800/" target="_blank" rel="noreferrer">33.2-1800</a> et seq.) shall not be less than three.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>, § 33.1-56.2; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[o];function s(l,h,c,d,g,f){return a(),t("div",null,i)}const m=e(n,[["render",s]]);export{p as __pageData,m as default};
