import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-906 Alternative procedure for abandonment of old highway or railroad crossing to extent of alteration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-906.md","filePath":"vacode/33.2-906.md"}'),r={name:"vacode/33.2-906.md"},n=o('<h1 id="§-33-2-906-alternative-procedure-for-abandonment-of-old-highway-or-railroad-crossing-to-extent-of-alteration" tabindex="-1">§ 33.2-906 Alternative procedure for abandonment of old highway or railroad crossing to extent of alteration. <a class="header-anchor" href="#§-33-2-906-alternative-procedure-for-abandonment-of-old-highway-or-railroad-crossing-to-extent-of-alteration" aria-label="Permalink to &quot;§ 33.2-906 Alternative procedure for abandonment of old highway or railroad crossing to extent of alteration.&quot;">​</a></h1><p>The Commissioner of Highways may declare any highway in the primary state highway system or any highway in the primary state highway system containing a highway-rail grade crossing abandoned when (i) it has been or is altered and a new highway that serves the same users as the old highway is constructed as a replacement and approved by the Commissioner of Highways or (ii) the Chief Engineer of the Department recommends that it is appropriate in connection with the completion of a construction or maintenance project. The old highways or the crossing may be abandoned to the extent of such alteration, but no further, by the entry by the Commissioner of Highways of such abandonment upon the records of the Department.</p><p>Code 1950, § 33-76.5; 1950, p. 730; 1952, c. 124; 1970, c. 322, § 33.1-148; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0152" target="_blank" rel="noreferrer">152</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[n];function s(h,c,d,l,f,g){return a(),t("div",null,i)}const _=e(r,[["render",s]]);export{m as __pageData,_ as default};
