import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-911 Permissible uses by counties of certain discontinued secondary highways.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-911.md","filePath":"vacode/33.2-911.md"}'),o={name:"vacode/33.2-911.md"},s=n('<h1 id="§-33-2-911-permissible-uses-by-counties-of-certain-discontinued-secondary-highways" tabindex="-1">§ 33.2-911 Permissible uses by counties of certain discontinued secondary highways. <a class="header-anchor" href="#§-33-2-911-permissible-uses-by-counties-of-certain-discontinued-secondary-highways" aria-label="Permalink to &quot;§ 33.2-911 Permissible uses by counties of certain discontinued secondary highways.&quot;">​</a></h1><p>Whenever a secondary highway is discontinued under § <a href="http://law.lis.virginia.gov/vacode/33.2-908/" target="_blank" rel="noreferrer">33.2-908</a>, the highway shall continue to be available for use by the public as a highway, unless it has been abandoned pursuant to this chapter or its use has been modified by an ordinance adopted pursuant to this section. The governing body of the county may by ordinance provide for use of a discontinued highway for any of the following purposes: (i) hiking or bicycle trails and paths or other nonvehicular transportation and recreation; (ii) greenway corridors for resource protection and biodiversity enhancement, with or without public ingress and egress; and (iii) access to historic, cultural, and educational sites.</p><p>1993, c. 349, § 33.1-152.1; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0129" target="_blank" rel="noreferrer">129</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),t=[s];function r(c,d,h,l,u,p){return i(),a("div",null,t)}const b=e(o,[["render",r]]);export{f as __pageData,b as default};
