import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 34-25 When homestead waived judgments and executions to so state.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/34-25.md","filePath":"vacode/34-25.md"}'),s={name:"vacode/34-25.md"},d=e("h1",{id:"§-34-25-when-homestead-waived-judgments-and-executions-to-so-state",tabindex:"-1"},[a("§ 34-25 When homestead waived judgments and executions to so state. "),e("a",{class:"header-anchor",href:"#§-34-25-when-homestead-waived-judgments-and-executions-to-so-state","aria-label":'Permalink to "§ 34-25 When homestead waived judgments and executions to so state."'},"​")],-1),i=e("p",null,'Whenever a judgment or decree is rendered on an instrument waiving the homestead or upon a demand against which the homestead cannot be claimed the court shall include in its judgment or decree words to the following effect, as the case may be: "Upon an instrument waiving the homestead," or "upon a claim against which the homestead cannot be demanded." This statement shall be endorsed upon the executions issued upon such judgments or decrees. In any action or suit when it is not apparent from the face of the pleadings that the demand is not subject to the homestead exemption the plaintiff shall not have the benefit of the foregoing provision of this section unless in his declaration he alleges that his demand is not subject to such homestead exemption. But no presumption of nonwaiver or that the judgment or decree was rendered upon a demand against which homestead could be claimed is to be drawn from the silence of any judgment, execution or decree on the matters provided for by this section.',-1),h=e("p",null,"Code 1919, § 6551.",-1),c=[d,i,h];function r(m,u,l,p,f,g){return o(),n("div",null,c)}const v=t(s,[["render",r]]);export{w as __pageData,v as default};
