import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 35.1-26 Cities, counties, and towns may by ordinance exempt certain fairs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/35.1-26.md","filePath":"vacode/35.1-26.md"}'),o={name:"vacode/35.1-26.md"},r=i('<h1 id="§-35-1-26-cities-counties-and-towns-may-by-ordinance-exempt-certain-fairs" tabindex="-1">§ 35.1-26 Cities, counties, and towns may by ordinance exempt certain fairs. <a class="header-anchor" href="#§-35-1-26-cities-counties-and-towns-may-by-ordinance-exempt-certain-fairs" aria-label="Permalink to &quot;§ 35.1-26 Cities, counties, and towns may by ordinance exempt certain fairs.&quot;">​</a></h1><p>The governing body of any county, city, or town may provide by ordinance that this title shall not apply to:</p><ol><li>Food booths at fairs, if such booths are promoted or sponsored by any political subdivision of the Commonwealth or by any charitable nonprofit organization or group thereof.</li></ol><p>The ordinance shall provide that the health officer of the county, city, or town in which the fair or activity is held, or a qualified person designated by him, shall exercise such supervision of the sale of food as the ordinance may prescribe.</p><p>Code 1950, § 35-38.1; 1964, c. 462; 1981, c. 468; 1983, c. 251; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0465" target="_blank" rel="noreferrer">465</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0317" target="_blank" rel="noreferrer">317</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0512" target="_blank" rel="noreferrer">512</a>.</p>',5),n=[r];function s(c,l,h,d,p,f){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
