import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 36-100 Notice and hearings on adoption of Code, amendments and repeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-100.md","filePath":"vacode/36-100.md"}'),t={name:"vacode/36-100.md"},r=n('<h1 id="§-36-100-notice-and-hearings-on-adoption-of-code-amendments-and-repeals" tabindex="-1">§ 36-100 Notice and hearings on adoption of Code, amendments and repeals. <a class="header-anchor" href="#§-36-100-notice-and-hearings-on-adoption-of-code-amendments-and-repeals" aria-label="Permalink to &quot;§ 36-100 Notice and hearings on adoption of Code, amendments and repeals.&quot;">​</a></h1><p>The adoption, amendment, or repeal of any Code provisions shall be exempt from the Administrative Process Act, Chapter 40 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) of Title 2.2, pursuant to subdivision A 12 of § <a href="http://law.lis.virginia.gov/vacode/2.2-4006/" target="_blank" rel="noreferrer">2.2-4006</a>. Before the adoption, amendment, or repeal of any Code provisions, the Board shall hold at least one public hearing. In addition to the notice requirement contained therein, the Board shall notify in writing the building official or, where none, the local governing body of every city or county in the Commonwealth. At any such hearing all persons desiring to do so shall be afforded an opportunity to present their views.</p><p>1972, c. 829; 1977, c. 613; 1991, c. 49; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0065" target="_blank" rel="noreferrer">65</a>.</p>',3),i=[r];function d(s,l,c,h,p,f){return a(),o("div",null,i)}const g=e(t,[["render",d]]);export{m as __pageData,g as default};
