import{_ as o,o as t,c as r,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 36-108 Board continued; members.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-108.md","filePath":"vacode/36-108.md"}'),i={name:"vacode/36-108.md"},n=e("h1",{id:"§-36-108-board-continued-members",tabindex:"-1"},[a("§ 36-108 Board continued; members. "),e("a",{class:"header-anchor",href:"#§-36-108-board-continued-members","aria-label":'Permalink to "§ 36-108 Board continued; members."'},"​")],-1),s=e("p",null,"There is hereby continued, in the Department, the State Building Code Technical Review Board, consisting of 14 members, appointed by the Governor subject to confirmation by the General Assembly. The members shall include one member who is a registered architect, selected from a slate presented by the Virginia Society of the American Institute of Architects; one member who is a professional engineer in private practice, selected from a slate presented by the Virginia Society of Professional Engineers; one member who is a residential builder, selected from a slate presented by the Home Builders Association of Virginia; one member who is a general contractor, selected from a slate presented by the Virginia Branch, Associated General Contractors of America; two members who have had experience in the field of enforcement of building regulations, selected from a slate presented by the Virginia Building and Code Officials Association; one member who is employed by a public agency as a fire prevention officer, selected from a slate presented by the Virginia Fire Chiefs Association; one member whose primary occupation is commercial or retail construction or operation and maintenance, selected from a slate presented by the Virginia chapters of Building Owners and Managers Association, International; one member whose primary occupation is residential, multifamily housing construction or operation and maintenance, selected from a slate presented by the Virginia chapters of the National Apartment Association; one member who is an electrical contractor who has held a Class A license for at least 10 years; one member who is a plumbing contractor who has held a Class A license for at least 10 years and one member who is a heating and cooling contractor who has held a Class A license for at least 10 years, both of whom are selected from a combined slate presented by the Virginia Association of Plumbing-Heating-Cooling Contractors and the Virginia Chapters of the Air Conditioning Contractors of America; and two members from the Commonwealth at large who may be members of local governing bodies. The members shall serve at the pleasure of the Governor.",-1),c=e("p",null,[a("1972, c. 829; 1974, c. 668; 1976, c. 484; 1977, cc. 92, 613; 1993, c. 626; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),a("; 2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0950",target:"_blank",rel:"noreferrer"},"950"),a(".")],-1),l=[n,s,c];function m(d,h,f,b,p,g){return t(),r("div",null,l)}const _=o(i,[["render",m]]);export{y as __pageData,_ as default};
