import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 36-139.1 Sale of real property for housing demonstration projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-139.1.md","filePath":"vacode/36-139.1.md"}'),a={name:"vacode/36-139.1.md"},n=t('<h1 id="§-36-139-1-sale-of-real-property-for-housing-demonstration-projects" tabindex="-1">§ 36-139.1 Sale of real property for housing demonstration projects. <a class="header-anchor" href="#§-36-139-1-sale-of-real-property-for-housing-demonstration-projects" aria-label="Permalink to &quot;§ 36-139.1 Sale of real property for housing demonstration projects.&quot;">​</a></h1><p>The Director is authorized to sell surplus real property belonging to the Commonwealth that is placed under the control of the Department for the purpose of establishing owner-occupied residential housing demonstration projects, with the prior written approval of the Governor or his designee, who shall first consider the written recommendation of the Director of the Department of General Services. The methods, terms and conditions of sale shall be developed in cooperation with the Department of General Services. Any contract of sale or deed of conveyance shall be approved as to form by the Attorney General or one of his deputies or assistant attorneys general. The proceeds from all such sales shall be handled in the manner prescribed in subsection I of § <a href="/vacode/2.2-1156/">2.2-1156</a>.</p><p>1988, c. 195; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0659" target="_blank" rel="noreferrer">659</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>.</p>',3),i=[n];function s(l,c,p,h,f,d){return r(),o("div",null,i)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
