import{_ as i,o as e,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 36-139.6 Additional powers and duties of Director; oversight of planning district commissions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-139.6.md","filePath":"vacode/36-139.6.md"}'),r={name:"vacode/36-139.6.md"},a=t('<h1 id="§-36-139-6-additional-powers-and-duties-of-director-oversight-of-planning-district-commissions" tabindex="-1">§ 36-139.6 Additional powers and duties of Director; oversight of planning district commissions. <a class="header-anchor" href="#§-36-139-6-additional-powers-and-duties-of-director-oversight-of-planning-district-commissions" aria-label="Permalink to &quot;§ 36-139.6 Additional powers and duties of Director; oversight of planning district commissions.&quot;">​</a></h1><p>The Director of the Department of Housing and Community Development shall have the following powers and duties relating to oversight of planning district commissions:</p><ol><li><p>To recommend to the Governor the level of state general appropriation funding for each planning district commission, taking into consideration the minimum funding level necessary for operation, the population of each district, and other factors considered appropriate;</p></li><li><p>To distribute state general appropriation funding to planning district commissions consistent with the provisions of this chapter and Chapter 42 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-4200/" target="_blank" rel="noreferrer">15.2-4200</a> et seq.) of Title 15.2;</p></li><li><p>To administer the Regional Cooperation Incentive Fund in accordance with § <a href="http://law.lis.virginia.gov/vacode/15.2-4217/" target="_blank" rel="noreferrer">15.2-4217</a>;</p></li><li><p>To provide technical assistance to planning district commissions regarding regional approaches to area-wide problems. Assistance may be initiated by the Department, individual local governments, or planning district commissions;</p></li><li><p>To require the submission of annual programmatic and financial information by each planning district commission in a format prescribed by the Director;</p></li><li><p>To prepare a biennial report to the Governor and the General Assembly which identifies the activities and other information deemed appropriate by the Director concerning planning district commissions, including findings as to planning district commissions which are not complying with Chapter 42 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-4200/" target="_blank" rel="noreferrer">15.2-4200</a> et seq.) of Title 15.2. Copies of the biennial report shall also be sent to the Commission on Local Government, Department of Small Business and Supplier Diversity, Department of Conservation and Recreation, Department of Environmental Quality, Department of Planning and Budget, Department of Transportation, Virginia Economic Development Partnership, and others upon request; and</p></li><li><p>To establish the Virginia Planning District Commission Council made up of the chairman or designated representative from each planning district commission to advise Department staff on programs, rules and regulations for the planning district commissions. Technical committees of planning district commission staff, state and local agency staff, and private sector individuals as needed, may be created.</p></li></ol><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0732" target="_blank" rel="noreferrer">732</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0598" target="_blank" rel="noreferrer">598</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0599" target="_blank" rel="noreferrer">599</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>.</p>',4),o=[a];function s(l,c,p,d,g,m){return e(),n("div",null,o)}const v=i(r,[["render",s]]);export{f as __pageData,v as default};
