import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 36-139 Powers and duties of Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-139.md","filePath":"vacode/36-139.md"}'),a={name:"vacode/36-139.md"},r=t('<h1 id="§-36-139-powers-and-duties-of-director" tabindex="-1">§ 36-139 Powers and duties of Director. <a class="header-anchor" href="#§-36-139-powers-and-duties-of-director" aria-label="Permalink to &quot;§ 36-139 Powers and duties of Director.&quot;">​</a></h1><p>The Director of the Department of Housing and Community Development shall have the following responsibilities:</p><ol><li><p>Collecting from the governmental subdivisions of the Commonwealth information relevant to their planning and development activities, boundary changes, changes of forms and status of government, intergovernmental agreements and arrangements, and such other information as he may deem necessary.</p></li><li><p>Making information available to communities, planning district commissions, service districts and governmental subdivisions of the Commonwealth.</p></li><li><p>Providing professional and technical assistance to, and cooperating with, any planning agency, planning district commission, service district, and governmental subdivision engaged in the preparation of development plans and programs, service district plans, or consolidation agreements.</p></li><li><p>Assisting the Governor in the providing of such state financial aid as may be appropriated by the General Assembly in accordance with § <a href="/vacode/15.2-4216/">15.2-4216</a>.</p></li><li><p>Administering federal grant assistance programs, including funds from the Appalachian Regional Commission, the Economic Development Administration and other such federal agencies, directed at promoting the development of the Commonwealth&#39;s communities and regions.</p></li><li><p>Developing state community development policies, goals, plans and programs for the consideration and adoption of the Board with the ultimate authority for adoption to rest with the Governor and the General Assembly.</p></li><li><p>Developing a Consolidated Plan to guide the development and implementation of housing programs and community development in the Commonwealth for the purpose of meeting the housing and community development needs of the Commonwealth and, in particular, those of low-income and moderate-income persons, families and communities.</p></li><li><p>Determining present and future housing requirements of the Commonwealth on an annual basis and revising the Consolidated Plan, as necessary to coordinate the elements of housing production to ensure the availability of housing where and when needed.</p></li><li><p>Assuming administrative coordination of the various state housing programs and cooperating with the various state agencies in their programs as they relate to housing.</p></li><li><p>Establishing public information and educational programs relating to housing; devising and administering programs to inform all citizens about housing and housing-related programs that are available on all levels of government; designing and administering educational programs to prepare families for home ownership and counseling them during their first years as homeowners; and promoting educational programs to assist sponsors in the development of low and moderate income housing as well as programs to lessen the problems of rental housing management.</p></li><li><p>Administering the provisions of the Industrialized Building Safety Law (§ <a href="/vacode/36-70/">36-70</a> et seq.).</p></li><li><p>Administering the provisions of the Uniform Statewide Building Code (§ <a href="/vacode/36-97/">36-97</a> et seq.).</p></li><li><p>Establishing and operating a Building Code Academy for the training of persons in the content, application, and intent of specified subject areas of the building and fire prevention regulations promulgated by the Board of Housing and Community Development.</p></li><li><p>Administering, in conjunction with the federal government, and promulgating any necessary regulations regarding energy standards for existing buildings as may be required pursuant to federal law.</p></li><li><p>Identifying and disseminating information to local governments about the availability and utilization of federal and state resources.</p></li><li><p>Administering, with the cooperation of the Department of Health, state assistance programs for public water supply systems.</p></li><li><p>Advising the Board on matters relating to policies and programs of the Virginia Housing Trust Fund.</p></li><li><p>Designing and establishing program guidelines to meet the purposes of the Virginia Housing Trust Fund and to carry out the policies and procedures established by the Board.</p></li><li><p>Preparing agreements and documents for loans and grants to be made from the Virginia Housing Trust Fund; soliciting, receiving, reviewing and selecting the applications for which loans and grants are to be made from such fund; directing the Virginia Housing Development Authority and the Department as to the closing and disbursing of such loans and grants and as to the servicing and collection of such loans; directing the Department as to the regulation and monitoring of the ownership, occupancy and operation of the housing developments and residential housing financed or assisted by such loans and grants; and providing direction and guidance to the Virginia Housing Development Authority as to the investment of moneys in such fund.</p></li><li><p>Establishing and administering program guidelines for a statewide homeless intervention program.</p></li><li><p>Administering 15 percent of the Low Income Home Energy Assistance Program (LIHEAP) Block Grant and any contingency funds awarded and carry over funds, furnishing home weatherization and associated services to low-income households within the Commonwealth in accordance with applicable federal law and regulations.</p></li><li><p>Developing a strategy concerning the expansion of affordable, accessible housing for older Virginians and Virginians with disabilities, including supportive services.</p></li><li><p>Serving as the Executive Director of the Commission on Local Government as prescribed in § <a href="/vacode/15.2-2901/">15.2-2901</a> and perform all other duties of that position as prescribed by law.</p></li><li><p>Developing a strategy, in consultation with the Virginia Housing Development Authority, for the creation and implementation of housing programs and community development for the purpose of meeting the housing needs of persons who have been released from federal, state, and local correctional facilities into communities.</p></li><li><p>Administering the Private Activity Bonds program in Chapter 50 (§ <a href="/vacode/15.2-5000/">15.2-5000</a> et seq.) of Title 15.2 jointly with the Virginia Small Business Financing Authority and the Virginia Housing Development Authority.</p></li><li><p>Developing a statement of tenant rights and responsibilities explaining in plain language the rights and responsibilities of tenants under the Virginia Residential Landlord and Tenant Act (§ <a href="/vacode/55.1-1200/">55.1-1200</a> et seq.) and maintaining such statement on the Department&#39;s website. The Director shall also develop and maintain on the Department&#39;s website a printable form to be signed by the parties to a written rental agreement acknowledging that the tenant has received from the landlord the statement of tenant rights and responsibilities as required by § <a href="/vacode/55.1-1204/">55.1-1204</a>. The Director may at any time amend the statement of tenant rights and responsibilities and such printable form as the Director deems necessary and appropriate. The statement of tenant rights and responsibilities shall contain a plain language explanation of the rights and responsibilities of tenants in at least 14-point type. The statement shall provide the telephone number and website address for the statewide legal aid organization and direct tenants with questions about their rights and responsibilities to contact such organization.</p></li><li><p>Developing a statement of tenant rights and responsibilities explaining in plain language the rights and responsibilities of tenants under the Virginia Manufactured Home Lot Rental Act (§ <a href="/vacode/55.1-1300/">55.1-1300</a> et seq.) and maintaining such statement on the Department&#39;s website. The Director shall also develop and maintain on the Department&#39;s website a printable form to be signed by the parties to a written rental agreement acknowledging that the tenant has received from the landlord the statement of tenant rights and responsibilities as required by § <a href="/vacode/55.1-1303/">55.1-1303</a>. The Director may at any time amend the statement of tenant rights and responsibilities and such printable form as the Director deems necessary and appropriate. The statement of tenant rights and responsibilities shall contain a plain language explanation of the rights and responsibilities in at least 14-point type. The statement shall provide the telephone number and website address for the statewide legal aid organization and direct tenants with questions about their rights and responsibilities to contact such organization.</p></li><li><p>Developing a sample termination notice that includes language referencing acceptance of rent with reservation by a landlord following a breach of a lease by a tenant in accordance with § <a href="/vacode/55.1-1250/">55.1-1250</a>. The sample termination notice shall be in at least 14-point type and shall be maintained on the Department&#39;s website.</p></li><li><p>Carrying out such other duties as may be necessary and convenient to the exercise of powers granted to the Department.</p></li></ol><p>1977, c. 613; 1978, cc. 737, 751; 1981, cc. 309, 315; 1982, c. 36; 1986, c. 427; 1988, c. 687; 1989, cc. 258, 279; 1992, c. 754; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0044" target="_blank" rel="noreferrer">44</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0693" target="_blank" rel="noreferrer">693</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0245" target="_blank" rel="noreferrer">245</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0197" target="_blank" rel="noreferrer">197</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0647" target="_blank" rel="noreferrer">647</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0741" target="_blank" rel="noreferrer">741</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0445" target="_blank" rel="noreferrer">445</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0985" target="_blank" rel="noreferrer">985</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0986" target="_blank" rel="noreferrer">986</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0091" target="_blank" rel="noreferrer">91</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>.</p>',4),o=[r];function s(l,d,g,h,p,c){return i(),n("div",null,o)}const u=e(a,[["render",s]]);export{f as __pageData,u as default};
