import{_ as n,o,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 36-143 Deposit of money; expenditures; investments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-143.md","filePath":"vacode/36-143.md"}'),s={name:"vacode/36-143.md"},a=e("h1",{id:"§-36-143-deposit-of-money-expenditures-investments",tabindex:"-1"},[t("§ 36-143 Deposit of money; expenditures; investments. "),e("a",{class:"header-anchor",href:"#§-36-143-deposit-of-money-expenditures-investments","aria-label":'Permalink to "§ 36-143 Deposit of money; expenditures; investments."'},"​")],-1),r=e("p",null,"All money transferred to the control of the HDA from the Fund shall be deposited in an account or accounts in banks or trust companies organized under the laws of the Commonwealth or in national banking associations located in Virginia or in savings institutions located in Virginia organized under the laws of the Commonwealth or the United States. The money in these accounts shall be paid by check signed by the Executive Director of the HDA or other officers or employees designated by the Commissioners of the HDA. All deposits of money shall, if required by the Director of the Department of Housing and Community Development, be secured in a manner determined by the Director of the Department of Housing and Community Development to be prudent. All banks, trust companies and savings and loan associations are authorized to give security for the deposits. Money in the Fund shall not be commingled with other money of the HDA. Money in the Fund not needed for immediate use or disbursement may be invested or reinvested by the HDA at the direction and guidance of the Director of the Department of Housing and Community Development in obligations or securities which are considered lawful investments for public funds under the laws of the Commonwealth. All interest and earnings accrued from investments of moneys from the Fund shall be used to increase the amount available in the Fund.",-1),d=e("p",null,[t("1988, c. 687; 1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0077",target:"_blank",rel:"noreferrer"},"77"),t("; 2013, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0754",target:"_blank",rel:"noreferrer"},"754"),t(".")],-1),l=[a,r,d];function c(h,m,u,f,p,g){return o(),i("div",null,l)}const y=n(s,[["render",c]]);export{v as __pageData,y as default};
