import{_ as o,o as s,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 36-148 Pledge of assets to secure bonds of the HDA.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-148.md","filePath":"vacode/36-148.md"}'),a={name:"vacode/36-148.md"},n=e("h1",{id:"§-36-148-pledge-of-assets-to-secure-bonds-of-the-hda",tabindex:"-1"},[t("§ 36-148 Pledge of assets to secure bonds of the HDA. "),e("a",{class:"header-anchor",href:"#§-36-148-pledge-of-assets-to-secure-bonds-of-the-hda","aria-label":'Permalink to "§ 36-148 Pledge of assets to secure bonds of the HDA."'},"​")],-1),h=e("p",null,"The HDA is empowered at any time and from time to time to transfer, upon the direction of the Director of the Department of Housing and Community Development, from the portion of the Fund under its control to banks or trust companies designated by the HDA any or all of the assets of the Fund to be held in trust as security for the payment of the principal of and premium, if any, and interest on any or all of the bonds of the HDA. The interests of the Fund in any assets so transferred shall be subordinate to the rights of the trustee under the pledge. To the extent funds are not available from other sources pledged for such purpose, any payments of principal and interest received on the assets transferred or held in trust may be applied by the trustee thereof to the payment of the principal of and premium, if any, and interest on such bonds of the HDA to which the assets have been pledged, and, if such payments are insufficient for such purpose, the trustee is empowered to sell any or all of such assets and apply the net proceeds from the sale to the payment of the principal of and premium, if any, and interest on such bonds of the HDA. Any assets of the Fund transferred in trust as set forth above and any payments of principal, interest or earnings received thereon shall remain part of the Fund but shall be subject to the pledge to secure the bonds of the HDA and shall be held by the trustee to which they are pledged until no longer required for such purpose by the terms of the pledge. On or before January 10 of each year, the HDA shall transfer, or shall cause the trustee to transfer, to the Fund any assets transferred or held in trust as set forth above which are no longer required to be held in trust pursuant to the terms of the pledge.",-1),d=e("p",null,[t("1988, c. 687; 2013, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0754",target:"_blank",rel:"noreferrer"},"754"),t(".")],-1),i=[n,h,d];function f(l,c,p,u,m,b){return s(),r("div",null,i)}const _=o(a,[["render",f]]);export{g as __pageData,_ as default};
