import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 36-149 Formation of corporation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-149.md","filePath":"vacode/36-149.md"}'),a={name:"vacode/36-149.md"},n=t('<h1 id="§-36-149-formation-of-corporation" tabindex="-1">§ 36-149 Formation of corporation. <a class="header-anchor" href="#§-36-149-formation-of-corporation" aria-label="Permalink to &quot;§ 36-149 Formation of corporation.&quot;">​</a></h1><p>For the purposes set forth in §§ <a href="http://law.lis.virginia.gov/vacode/36-141/" target="_blank" rel="noreferrer">36-141</a> through <a href="http://law.lis.virginia.gov/vacode/36-148/" target="_blank" rel="noreferrer">36-148</a>, the HDA may form a corporation to administer and manage the portion of the Fund under its control in accordance with the provisions thereof and to exercise and perform any or all of the powers and duties provided therein. Such corporation shall be under the control and supervision of the HDA. Such corporation shall have such of the powers and duties of the HDA set forth in the above referenced sections as HDA shall delegate to such corporation and such other powers and duties as shall be provided by the documents and instruments creating and governing the corporation and by applicable state law. The provisions of the above referenced sections authorizing or requiring the HDA to exercise any power or to perform any duty shall be deemed to authorize or require such corporation to exercise any such power or to perform any such duty as shall be delegated by the HDA to such corporation.</p><p>1988, c. 687; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>.</p>',3),i=[n];function s(c,h,d,p,l,f){return o(),r("div",null,i)}const m=e(a,[["render",s]]);export{_ as __pageData,m as default};
