import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 36-155 Grants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-155.md","filePath":"vacode/36-155.md"}'),n={name:"vacode/36-155.md"},i=r('<h1 id="§-36-155-grants" tabindex="-1">§ 36-155 Grants. <a class="header-anchor" href="#§-36-155-grants" aria-label="Permalink to &quot;§ 36-155 Grants.&quot;">​</a></h1><p>Except as otherwise provided in this chapter, money in the Fund shall be used to make grants to local governments to finance the acquisition, removal, rehabilitation, repair or demolition of derelict structures. No grant shall exceed $1,000,000. Each grant shall be conditioned upon a 100 percent match of funds by the local government. The Board shall develop guidelines for the administration of the grant program established by this chapter.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP1018" target="_blank" rel="noreferrer">1018</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0795" target="_blank" rel="noreferrer">795</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0577" target="_blank" rel="noreferrer">577</a>.</p>',3),o=[i];function l(s,c,h,d,p,_){return t(),a("div",null,o)}const m=e(n,[["render",l]]);export{f as __pageData,m as default};
