import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 36-172 Claiming first-time home buyer status.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-172.md","filePath":"vacode/36-172.md"}'),o={name:"vacode/36-172.md"},n=i('<h1 id="§-36-172-claiming-first-time-home-buyer-status" tabindex="-1">§ 36-172 Claiming first-time home buyer status. <a class="header-anchor" href="#§-36-172-claiming-first-time-home-buyer-status" aria-label="Permalink to &quot;§ 36-172 Claiming first-time home buyer status.&quot;">​</a></h1><p>A. The account holder shall be responsible for the use or application of moneys or funds in an account for which the account holder claims first-time home buyer savings account status.</p><p>B. The account holder shall (i) not use moneys or funds held in an account to pay expenses of administering the account, except that a service fee may be deducted from the account by a financial institution; (ii) maintain documentation, which may include the settlement statement, of the segregation of moneys or funds in separate accounts and documentation of eligible costs for the purchase of a single-family residence in the Commonwealth; (iii) file, with the account holder&#39;s Virginia income tax return, forms developed by the Department of Taxation regarding treatment of the account as a first-time home buyer savings account under this chapter, along with the Form 1099 issued by the financial institution for such account; and (iv) remit to the Department of Taxation the tax on any amounts (a) added to individual income pursuant to subdivision 6 of § <a href="/vacode/58.1-322.01/">58.1-322.01</a> or (b) recaptured pursuant to subdivision 25 of § <a href="/vacode/58.1-322.02/">58.1-322.02</a>.</p><p>C. The Tax Commissioner shall develop guidelines applicable to account holders to implement the provisions of this chapter. Such guidelines shall be exempt from the provisions of the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.). Such guidelines shall not apply to, or impose administrative, reporting, or other obligations or requirements on, financial institutions-related accounts for which first-time home buyer savings account status is claimed by the account holder.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, § 55-556; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0444" target="_blank" rel="noreferrer">444</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),r=[n];function s(c,h,l,u,m,d){return t(),a("div",null,r)}const g=e(o,[["render",s]]);export{p as __pageData,g as default};
