import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 36-173 Tax exemption; conditions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-173.md","filePath":"vacode/36-173.md"}'),n={name:"vacode/36-173.md"},i=o('<h1 id="§-36-173-tax-exemption-conditions" tabindex="-1">§ 36-173 Tax exemption; conditions. <a class="header-anchor" href="#§-36-173-tax-exemption-conditions" aria-label="Permalink to &quot;§ 36-173 Tax exemption; conditions.&quot;">​</a></h1><p>A. All interest or other income earned attributable to an account shall be excluded from the Virginia taxable income of the account holder as provided under subdivision 25 of § <a href="/vacode/58.1-322.02/">58.1-322.02</a>.</p><p>B. There shall be an aggregate limit of $50,000 per account on the amount of principal for which the account holder may claim first-time home buyer savings account status. Only cash and marketable securities may be contributed to an account.</p><p>C. Subject to the aggregate limit on the amount of principal that may be contributed to an account pursuant to subsection B, there shall be a limitation of $150,000 on the amount of principal and interest or other income on the principal that may be retained within an account.</p><p>D. An account holder shall be subject to Virginia income tax pursuant to subdivision 6 of § <a href="/vacode/58.1-322.01/">58.1-322.01</a> to the extent of any loss deducted as a capital loss by the individual for federal income tax purposes attributable to the person&#39;s account.</p><p>E. Upon being furnished proof of the death of the account holder, a financial institution shall distribute the principal and accumulated interest or other income in the account in accordance with the terms of the contract governing the account.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, § 55-557; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0444" target="_blank" rel="noreferrer">444</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',7),r=[i];function c(s,l,h,d,p,u){return t(),a("div",null,r)}const b=e(n,[["render",c]]);export{m as __pageData,b as default};
