import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 36-19.2 Powers limited by necessity for authority from or approval by governing body; public hearing on proposed budget.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-19.2.md","filePath":"vacode/36-19.2.md"}'),a={name:"vacode/36-19.2.md"},i=t('<h1 id="§-36-19-2-powers-limited-by-necessity-for-authority-from-or-approval-by-governing-body-public-hearing-on-proposed-budget" tabindex="-1">§ 36-19.2 Powers limited by necessity for authority from or approval by governing body; public hearing on proposed budget. <a class="header-anchor" href="#§-36-19-2-powers-limited-by-necessity-for-authority-from-or-approval-by-governing-body-public-hearing-on-proposed-budget" aria-label="Permalink to &quot;§ 36-19.2 Powers limited by necessity for authority from or approval by governing body; public hearing on proposed budget.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="/vacode/36-19/">36-19</a>, no authority permitted to transact business and exercise powers as provided in § <a href="/vacode/36-4/">36-4</a> shall make any contract for the construction of any additional housing not authorized or approved by the governing body on April 1, 1952, or acquire land for, or purchase material for the construction or installation of, any sewerage, streets, sidewalks, lights, power, water, or any other facilities for any additional housing not authorized or approved on such date, unless and until such additional housing has been authorized or approved by the governing body of the locality in which the authority is authorized to transact business and exercise powers, provided that this section shall not affect or impair the provisions of § <a href="/vacode/36-19.1/">36-19.1</a>.</p><p>B. Before any authority gives final approval to (i) its budget or (ii) any request for funding for submission to the governing body, the authority shall hold at least one public hearing to receive the views of citizens within the area of operation of the authority. The authority shall cause public notice to be given at least 10 days prior to any hearing by publication in a newspaper having a general circulation within the area of operation of the authority.</p><p>1952, c. 427; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0342" target="_blank" rel="noreferrer">342</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0561" target="_blank" rel="noreferrer">561</a>.</p>',4),n=[i];function s(h,l,p,c,d,g){return o(),r("div",null,n)}const b=e(a,[["render",s]]);export{f as __pageData,b as default};
