import{_ as o,o as n,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 36-22 Rentals and tenant selection.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-22.md","filePath":"vacode/36-22.md"}'),i={name:"vacode/36-22.md"},s=e("h1",{id:"§-36-22-rentals-and-tenant-selection",tabindex:"-1"},[t("§ 36-22 Rentals and tenant selection. "),e("a",{class:"header-anchor",href:"#§-36-22-rentals-and-tenant-selection","aria-label":'Permalink to "§ 36-22 Rentals and tenant selection."'},"​")],-1),r=e("p",null,"In the operation or management of housing projects an authority shall at all times observe the following duties with respect to rentals and tenant selection: (a) it may rent or lease the dwelling accommodations therein only to persons of low income and at rentals within the financial reach of such persons of low income; (b) it may rent or lease to a tenant dwelling accommodations consisting of the number of rooms (but no greater number) which it deems necessary to provide safe and sanitary accommodations to the proposed occupants thereof, without overcrowding; (c) it shall not accept any person as a tenant in any housing project if the person or persons who would occupy the dwelling accommodations have an aggregate annual income in excess of the maximum income limits established by the authority for the purpose of determining what persons or families are of low income; and (d) at least thirty days before establishing or changing the aggregate annual maximum income limits for the purpose of determining what persons or families are persons of low income, an authority shall notify the governing body of the city or county thereof.",-1),c=e("p",null,[t("Nothing contained in this section or § "),e("a",{href:"http://law.lis.virginia.gov/vacode/36-21/",target:"_blank",rel:"noreferrer"},"36-21"),t(" shall be construed as limiting the power of an authority to vest in an obligee the right, in the event of a default by the authority, to take possession of a housing project or cause the appointment of a receiver thereof, free from all the restrictions imposed by this section or § "),e("a",{href:"http://law.lis.virginia.gov/vacode/36-21/",target:"_blank",rel:"noreferrer"},"36-21"),t(".")],-1),l=e("p",null,"1938, p. 452; Michie Code 1942, § 3145(10); 1958, c. 116; 1972, cc. 466, 782.",-1),h=[s,r,c,l];function d(m,f,p,g,u,_){return n(),a("div",null,h)}const v=o(i,[["render",d]]);export{w as __pageData,v as default};
