import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 36-27.2 Limitations on certain housing authorities; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-27.2.md","filePath":"vacode/36-27.2.md"}'),r={name:"vacode/36-27.2.md"},o=i('<h1 id="§-36-27-2-limitations-on-certain-housing-authorities-exception" tabindex="-1">§ 36-27.2 Limitations on certain housing authorities; exception. <a class="header-anchor" href="#§-36-27-2-limitations-on-certain-housing-authorities-exception" aria-label="Permalink to &quot;§ 36-27.2 Limitations on certain housing authorities; exception.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/36-27/" target="_blank" rel="noreferrer">36-27</a> and except as provided in subsection B, no housing authority transacting business and exercising powers as provided in § <a href="http://law.lis.virginia.gov/vacode/36-4/" target="_blank" rel="noreferrer">36-4</a> in the City of Norfolk shall be authorized after July 1, 2007, to acquire by the exercise of the power of eminent domain, any real property located within the boundaries set forth in the Conservation and Redevelopment Plan (the Plan) for the East Ocean View Conservation and Redevelopment Project adopted July 1989, as amended by Amendment No. 1 to such plan adopted September 1992.</p><p>The provisions of this subsection shall not apply to any such real property for which an offer has been made by such housing authority or for which such authority has initiated condemnation proceedings prior to July 1, 2007.</p><p>B. The City of Norfolk and the housing authority shall not be precluded from adopting (i) a new redevelopment plan, in accordance with the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/36-27/" target="_blank" rel="noreferrer">36-27</a>, <a href="http://law.lis.virginia.gov/vacode/36-49/" target="_blank" rel="noreferrer">36-49</a>, and <a href="http://law.lis.virginia.gov/vacode/36-51/" target="_blank" rel="noreferrer">36-51</a>, which designates a redevelopment area that includes real property for acquisition that was previously included within the Plan, or (ii) a new conservation plan, in accordance with the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/36-27/" target="_blank" rel="noreferrer">36-27</a>, <a href="http://law.lis.virginia.gov/vacode/36-50.1/" target="_blank" rel="noreferrer">36-50.1</a>, and <a href="http://law.lis.virginia.gov/vacode/36-51.1/" target="_blank" rel="noreferrer">36-51.1</a>, which designates a conservation area that includes real property for acquisition that was previously included within the Plan.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0540" target="_blank" rel="noreferrer">540</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0786" target="_blank" rel="noreferrer">786</a>.</p>',5),n=[o];function s(h,l,c,d,p,f){return a(),t("div",null,n)}const u=e(r,[["render",s]]);export{v as __pageData,u as default};
