import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 36-85.18 Powers and duties of Manufactured Housing Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-85.18.md","filePath":"vacode/36-85.18.md"}'),t={name:"vacode/36-85.18.md"},s=o('<h1 id="§-36-85-18-powers-and-duties-of-manufactured-housing-board" tabindex="-1">§ 36-85.18 Powers and duties of Manufactured Housing Board. <a class="header-anchor" href="#§-36-85-18-powers-and-duties-of-manufactured-housing-board" aria-label="Permalink to &quot;§ 36-85.18 Powers and duties of Manufactured Housing Board.&quot;">​</a></h1><p>The Virginia Manufactured Housing Board shall have the following powers and duties:</p><ol><li><p>To issue licenses to manufacturers, dealers, brokers, and salespersons;</p></li><li><p>To require that an adequate recovery fund be established for all regulants;</p></li><li><p>To receive and resolve complaints from buyers of manufactured homes and from persons in the manufactured housing industry;</p></li><li><p>To promulgate regulations in accordance with the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) as are necessary to carry out the provisions of this chapter, including but not limited to the licensure of manufactured home brokers, dealers, manufacturers, and salespersons and the relicensure of manufactured home brokers, dealers, manufacturers, or salespersons after license revocation or nonrenewal;</p></li><li><p>To make case decisions in accordance with the Administrative Process Act as are necessary to carry out the provisions of this chapter; and</p></li><li><p>To levy and collect fees that are sufficient to cover the expenses for the administration of this chapter by the Board and the Department. Such fees may be levied and collected on a per unit sold basis, a percentage basis, an annual per dealer basis, or a combination thereof.</p></li></ol><p>1991, c. 555; 1992, c. 223; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0671" target="_blank" rel="noreferrer">671</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0350" target="_blank" rel="noreferrer">350</a>.</p>',4),n=[s];function i(c,d,l,u,f,h){return a(),r("div",null,n)}const _=e(t,[["render",i]]);export{m as __pageData,_ as default};
