import{_ as a,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 36-85.21 Notice and hearing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-85.21.md","filePath":"vacode/36-85.21.md"}'),i={name:"vacode/36-85.21.md"},r=e("h1",{id:"§-36-85-21-notice-and-hearing",tabindex:"-1"},[t("§ 36-85.21 Notice and hearing. "),e("a",{class:"header-anchor",href:"#§-36-85-21-notice-and-hearing","aria-label":'Permalink to "§ 36-85.21 Notice and hearing."'},"​")],-1),s=e("p",null,"The Board shall not suspend, revoke, or deny a license or refuse the renewal of a license, or impose a civil penalty, until a written notice of the complaint has been furnished to the regulant or applicant against whom the same is directed, and a hearing thereon has been held before the Board. Reasonable written notice of the time and place of the hearing shall be given to the regulant or applicant by certified mail to his last known address, as shown on the license or other record of information in possession of the Board. At any such hearing, the regulant or applicant shall have the right to be heard in person or through counsel. After the hearing, the Board shall have the power to deny, suspend, revoke or refuse to renew the license in question for violation of the provisions of this chapter. Immediate notice of any such action by the Board shall be given to the regulant or applicant in the same manner as provided herein for furnishing notice of hearing.",-1),h=e("p",null,[t("In the event of a conflict between the provisions of this section and the Administrative Process Act (§ "),e("a",{href:"http://law.lis.virginia.gov/vacode/2.2-4000/",target:"_blank",rel:"noreferrer"},"2.2-4000"),t(" et seq.), the provisions of the Administrative Process Act shall govern.")],-1),c=e("p",null,"1991, c. 555; 1992, c. 223.",-1),l=[r,s,h,c];function d(p,f,g,u,v,_){return o(),n("div",null,l)}const w=a(i,[["render",d]]);export{b as __pageData,w as default};
