import{_ as t,o as r,c as a,z as e,a as i}from"./chunks/framework.9482e208.js";const W=JSON.parse('{"title":"§ 36-85.25 Warranty service.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/36-85.25.md","filePath":"vacode/36-85.25.md"}'),n={name:"vacode/36-85.25.md"},o=e("h1",{id:"§-36-85-25-warranty-service",tabindex:"-1"},[i("§ 36-85.25 Warranty service. "),e("a",{class:"header-anchor",href:"#§-36-85-25-warranty-service","aria-label":'Permalink to "§ 36-85.25 Warranty service."'},"​")],-1),s=e("p",null,"When a service agreement exists between or among a manufacturer, dealer, and supplier to provide warranty service, the agreement shall specify which such responsible party is to remedy warranty defects. Every such service agreement shall be in writing. Nothing contained in such an agreement shall relieve the responsible party, as provided in this chapter, of responsibility to perform warranty service. However, any responsible party undertaking such an agreement to perform the warranty service obligations of another shall thereby become responsible both to that other responsible party and to the buyer for his failure to adequately perform as agreed.",-1),h=e("p",null,"When no service agreement exists for warranty service, the responsible party as designated by the provisions of this chapter is responsible for remedying the warranty defects.",-1),c=e("p",null,"A defect shall be remedied within forty-five days of receipt of the written notification of the warranty claim, unless the claim is unreasonable or a bona fide reason exists for not remedying the defect within the forty-five-day period. The responsible party shall respond to the claimant in writing with a copy to the Board stating what further action is contemplated by the responsible party. Notwithstanding the foregoing provisions of this section, defects which constitute an imminent safety hazard to life and health shall be remedied within three days of receipt of the written notification of the warranty claim. An imminent safety hazard to life and health shall include but not be limited to (i) inadequate heating in freezing weather; (ii) failure of sanitary facilities; (iii) electrical shock or leaking gas; or (iv) major structural failure. The Board may suspend this three-day time period in the event of widespread defects or damage resulting from adverse weather conditions or other natural catastrophes.",-1),l=e("p",null,"When the person remedying the defect is not the responsible party as designated by the provisions of this chapter, he shall be entitled to reasonable compensation paid to him by the responsible party. Conduct which coerces or requires a nonresponsible party to perform warranty service is a violation of this chapter.",-1),d=e("p",null,"Warranty service shall be performed at the site at which the manufactured home is initially delivered to the buyer, except for components which can be removed for service without undue inconvenience to the buyer.",-1),p=e("p",null,"Any responsible party shall have the right to complain to the Board when warranty service obligations under this chapter are not being enforced.",-1),f=e("p",null,"1991, c. 555; 1992, c. 223.",-1),y=[o,s,h,c,l,d,p,f];function m(u,b,v,w,g,_){return r(),a("div",null,y)}const k=t(n,[["render",m]]);export{W as __pageData,k as default};
