import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 37.2-319 Administration of Behavioral Health and Developmental Services Trust Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-319.md","filePath":"vacode/37.2-319.md"}'),i={name:"vacode/37.2-319.md"},n=r('<h1 id="§-37-2-319-administration-of-behavioral-health-and-developmental-services-trust-fund" tabindex="-1">§ 37.2-319 Administration of Behavioral Health and Developmental Services Trust Fund. <a class="header-anchor" href="#§-37-2-319-administration-of-behavioral-health-and-developmental-services-trust-fund" aria-label="Permalink to &quot;§ 37.2-319 Administration of Behavioral Health and Developmental Services Trust Fund.&quot;">​</a></h1><p>A. The Fund shall be administered by the Commissioner. Moneys in the Fund shall be used for mental health, developmental, or substance abuse services and to facilitate transition of individuals with intellectual disability from state training centers to community-based services. Notwithstanding any other provision of law, the net proceeds from the sale of any vacant buildings and land shall first be used to (i) deliver mental health, developmental, and substance abuse services within the same service area where the sold buildings and land were located to ensure the same level of mental health, developmental, and substance abuse services as before the sale and (ii) provide benefits pursuant to the Workforce Transition Act of 1995 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3200/" target="_blank" rel="noreferrer">2.2-3200</a> et seq.) to those persons who were employees of the Commonwealth and, as a result of the sale, are no longer employed by the Commonwealth or are otherwise negatively affected by the sale.</p><p>B. For each fiscal year starting with the Commonwealth&#39;s 2011-2012 fiscal year, any funds directed to be deposited into the Fund pursuant to the general appropriation act shall be appropriated for financing (i) a broad array of community-based services including but not limited to Intellectual Disability Home and Community Based Waiver services or (ii) appropriate community housing, for the purpose of transitioning individuals with intellectual disability from state training centers to community-based care.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0569" target="_blank" rel="noreferrer">569</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0606" target="_blank" rel="noreferrer">606</a>, § 2.1-814; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § 37.1-260; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0724" target="_blank" rel="noreferrer">724</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',4),o=[n];function l(s,h,d,c,f,g){return a(),t("div",null,o)}const u=e(i,[["render",l]]);export{v as __pageData,u as default};
