import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 37.2-427 Mistreatment of individuals receiving services in hospital or training center.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-427.md","filePath":"vacode/37.2-427.md"}'),t={name:"vacode/37.2-427.md"},n=a('<h1 id="§-37-2-427-mistreatment-of-individuals-receiving-services-in-hospital-or-training-center" tabindex="-1">§ 37.2-427 Mistreatment of individuals receiving services in hospital or training center. <a class="header-anchor" href="#§-37-2-427-mistreatment-of-individuals-receiving-services-in-hospital-or-training-center" aria-label="Permalink to &quot;§ 37.2-427 Mistreatment of individuals receiving services in hospital or training center.&quot;">​</a></h1><p>It shall be unlawful for any officer or employee of any hospital or training center or other person to maltreat or misuse any individual who is receiving services in any hospital or training center or who is on a day pass, family visit, or trial visit from a hospital or training center. Any officer or employee of any hospital or training center or other person who maltreats or misuses any individual who is receiving services in any hospital or training center or who is on a day pass, family visit, or trial visit from a hospital or training center is guilty of a Class 1 misdemeanor.</p><p>Code 1950, § 37-16; 1950, p. 901; 1968, c. 477, § 37.1-150; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),o=[n];function s(l,c,p,d,f,h){return i(),r("div",null,o)}const _=e(t,[["render",s]]);export{g as __pageData,_ as default};
