import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 37.2-428 Aiding and abetting in escapes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-428.md","filePath":"vacode/37.2-428.md"}'),n={name:"vacode/37.2-428.md"},r=t('<h1 id="§-37-2-428-aiding-and-abetting-in-escapes" tabindex="-1">§ 37.2-428 Aiding and abetting in escapes. <a class="header-anchor" href="#§-37-2-428-aiding-and-abetting-in-escapes" aria-label="Permalink to &quot;§ 37.2-428 Aiding and abetting in escapes.&quot;">​</a></h1><p>It shall be unlawful for any officer or employee of any hospital or training center or any other person to aid or abet in the escape or secretion of any lawfully admitted individual receiving services in any hospital or training center, while the individual is in the hospital or training center or on a day pass, family visit, trial visit, bond or escapement, or to willfully fail or refuse to return an individual on a day pass, family visit, or trial visit under his care and custody to any hospital or training center in which he is receiving services, having given written obligation to do so, when directed in writing to do so by the director of the hospital or training center. Any such officer or employee of any hospital or training center or any other person is guilty of a Class 1 misdemeanor.</p><p>Code 1950, § 37-228; 1968, c. 477, § 37.1-151; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),o=[r];function s(l,c,d,p,g,h){return i(),a("div",null,o)}const v=e(n,[["render",s]]);export{_ as __pageData,v as default};
