import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 37.2-430 Providing alcoholic beverages to individuals receiving services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-430.md","filePath":"vacode/37.2-430.md"}'),o={name:"vacode/37.2-430.md"},c=a('<h1 id="§-37-2-430-providing-alcoholic-beverages-to-individuals-receiving-services" tabindex="-1">§ 37.2-430 Providing alcoholic beverages to individuals receiving services. <a class="header-anchor" href="#§-37-2-430-providing-alcoholic-beverages-to-individuals-receiving-services" aria-label="Permalink to &quot;§ 37.2-430 Providing alcoholic beverages to individuals receiving services.&quot;">​</a></h1><p>It shall be unlawful for any person to sell or give alcoholic beverages to any individual receiving services in any hospital or training center, bring alcoholic beverages onto the premises of the hospital or training center, administer alcoholic beverages to any individual receiving services, or place alcoholic beverages or cause them to be placed where any individual receiving services may access them, except if the alcoholic beverages are prescribed by the director or physicians of the hospital or training center. Any such person is guilty of a Class 1 misdemeanor.</p><p>Code 1950, § 37-230; 1968, c. 477, § 37.1-153; 1972, c. 639; 1976, c. 671; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),t=[c];function n(s,l,v,d,g,h){return i(),r("div",null,t)}const f=e(o,[["render",n]]);export{_ as __pageData,f as default};
