import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 37.2-507 Data collection on children and adolescents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-507.md","filePath":"vacode/37.2-507.md"}'),n={name:"vacode/37.2-507.md"},r=o('<h1 id="§-37-2-507-data-collection-on-children-and-adolescents" tabindex="-1">§ 37.2-507 Data collection on children and adolescents. <a class="header-anchor" href="#§-37-2-507-data-collection-on-children-and-adolescents" aria-label="Permalink to &quot;§ 37.2-507 Data collection on children and adolescents.&quot;">​</a></h1><p>Every community services board shall submit to the Department information on children under the age of 14 and adolescents ages 14 through 17 for whom admission to an inpatient acute care psychiatric or residential treatment facility licensed pursuant to Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/37.2-403/" target="_blank" rel="noreferrer">37.2-403</a> et seq.) of Chapter 4 of this title, excluding group homes, was sought but was unable to be obtained by the board. Information to be submitted shall include:</p><p>a. The child or adolescent&#39;s date of birth;</p><p>b. Date admission was attempted; and</p><p>c. Reason the child or adolescent could not be admitted to the facility.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0619" target="_blank" rel="noreferrer">619</a>, § 37.1-197.3; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>.</p>',6),i=[r];function l(c,s,d,h,p,_){return t(),a("div",null,i)}const u=e(n,[["render",l]]);export{g as __pageData,u as default};
