import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 37.2-720 When collection of expenses not required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-720.md","filePath":"vacode/37.2-720.md"}'),r={name:"vacode/37.2-720.md"},n=o('<h1 id="§-37-2-720-when-collection-of-expenses-not-required" tabindex="-1">§ 37.2-720 When collection of expenses not required. <a class="header-anchor" href="#§-37-2-720-when-collection-of-expenses-not-required" aria-label="Permalink to &quot;§ 37.2-720 When collection of expenses not required.&quot;">​</a></h1><p>This article shall not be held or construed to require the Department to collect the expenses of the care, treatment or training, and maintenance of any indigent individual receiving services from that individual or from any person liable for him when investigation discloses that the indigent individual or person liable for his support is without financial means or that such payment would work a hardship on the individual or his family. Neither shall it be the duty or obligation of the Department to institute any proceedings provided for in this article to effect collection where investigation discloses that proceedings would be without effect or would work a hardship on the individual receiving services or the person liable for his support.</p><p>Code 1950, § 37-125.13; 1968, c. 477, § 37.1-116; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),a=[n];function s(l,c,h,d,p,f){return t(),i("div",null,a)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
