import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 37.2-805 (Effective October 1, 2022) Voluntary admission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-805.md","filePath":"vacode/37.2-805.md"}'),i={name:"vacode/37.2-805.md"},o=r('<h1 id="§-37-2-805-effective-october-1-2022-voluntary-admission" tabindex="-1">§ 37.2-805 (Effective October 1, 2022) Voluntary admission. <a class="header-anchor" href="#§-37-2-805-effective-october-1-2022-voluntary-admission" aria-label="Permalink to &quot;§ 37.2-805 (Effective October 1, 2022) Voluntary admission.&quot;">​</a></h1><p>Any state facility shall admit any person requesting admission who has been (i) screened by the community services board or behavioral health authority that serves the county or city where the person resides or, if impractical, where the person is located, (ii) examined by a physician on the staff of the state facility, and (iii) deemed by the board or authority and the state facility physician to be in need of treatment, training, or habilitation in a state facility. Upon motion of the treating physician, a family member or personal representative of the person, or the community services board serving the county or city where the facility is located, the county or city where the person resides, or the county or city where the person receives treatment, a hearing shall be held prior to the release date of any person who has been the subject of a temporary detention order and voluntarily admitted himself in accordance with subsection B of § <a href="/vacode/37.2-814/">37.2-814</a> to determine whether such person should be ordered to mandatory outpatient treatment pursuant to subsection D of § <a href="/vacode/37.2-817.01/">37.2-817.01</a>, except that such 36-month period shall not include any time during which the person was receiving inpatient psychiatric treatment or was incarcerated, as established by evidence admitted at the hearing, upon his release if such person, on at least two previous occasions within 36 months preceding the date of the hearing, has been (a) the subject of a temporary detention order and voluntarily admitted himself in accordance with subsection B of § <a href="/vacode/37.2-814/">37.2-814</a> or (b) involuntarily admitted pursuant to § <a href="/vacode/37.2-817/">37.2-817</a>. A district court judge or special justice shall hold the hearing within 72 hours after receiving the motion for a mandatory outpatient treatment order; however, if the 72-hour period expires on a Saturday, Sunday, or legal holiday, the hearing shall be held by the close of business on the next day that is not a Saturday, Sunday, or legal holiday.</p><p>Code 1950, § 37-113; 1950, p. 915; 1964, c. 640; 1968, c. 477, § 37.1-65; 1970, c. 46; 1972, cc. 639, 823; 1976, c. 671; 1980, c. 582; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0300" target="_blank" rel="noreferrer">300</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0179" target="_blank" rel="noreferrer">179</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>.</p>',3),n=[o];function s(h,c,l,d,f,p){return t(),a("div",null,n)}const m=e(i,[["render",s]]);export{y as __pageData,m as default};
