import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 37.2-811 Emergency treatment of inmates in the custody of local correctional facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-811.md","filePath":"vacode/37.2-811.md"}'),i={name:"vacode/37.2-811.md"},n=t('<h1 id="§-37-2-811-emergency-treatment-of-inmates-in-the-custody-of-local-correctional-facilities" tabindex="-1">§ 37.2-811 Emergency treatment of inmates in the custody of local correctional facilities. <a class="header-anchor" href="#§-37-2-811-emergency-treatment-of-inmates-in-the-custody-of-local-correctional-facilities" aria-label="Permalink to &quot;§ 37.2-811 Emergency treatment of inmates in the custody of local correctional facilities.&quot;">​</a></h1><p>A. In any case in which temporary detention is ordered pursuant to § <a href="http://law.lis.virginia.gov/vacode/37.2-809/" target="_blank" rel="noreferrer">37.2-809</a> upon petition of a person having custody of an inmate in accordance with subdivision A 2 of § <a href="http://law.lis.virginia.gov/vacode/19.2-169.6/" target="_blank" rel="noreferrer">19.2-169.6</a>, the magistrate executing the temporary detention order shall place the person in a hospital designated by the Commissioner as appropriate for treatment and evaluation of persons under a criminal charge or, if such facility is not available, the inmate shall be detained in a local correctional facility or other place of confinement for persons charged with criminal offenses and shall be transferred to such hospital as soon as possible thereafter.</p><p>B. The hearing shall be held, upon notice to the attorney for the inmate, either (i) before the court having jurisdiction over the inmate&#39;s case or (ii) before a district court judge or special justice in accordance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/37.2-820/" target="_blank" rel="noreferrer">37.2-820</a>, in which case the inmate shall be represented by counsel as specified in § <a href="http://law.lis.virginia.gov/vacode/37.2-814/" target="_blank" rel="noreferrer">37.2-814</a>.</p><p>1974, c. 351, § 37.1-67.1; 1975, cc. 237, 433; 1976, c. 671; 1980, c. 582; 1981, c. 463; 1986, cc. 478, 629; 1987, c. 96; 1988, c. 98; 1989, c. 716; 1990, cc. 429, 728; 1991, c. 159; 1992, c. 566; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0893" target="_blank" rel="noreferrer">893</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0611" target="_blank" rel="noreferrer">611</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0737" target="_blank" rel="noreferrer">737</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0340" target="_blank" rel="noreferrer">340</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0406" target="_blank" rel="noreferrer">406</a>.</p>',4),o=[n];function c(l,s,f,h,g,p){return r(),a("div",null,o)}const _=e(i,[["render",c]]);export{v as __pageData,_ as default};
