import{_ as i,o as a,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 37.2-828 Receiving and maintaining federal prisoners in state facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-828.md","filePath":"vacode/37.2-828.md"}'),r={name:"vacode/37.2-828.md"},n=e("h1",{id:"§-37-2-828-receiving-and-maintaining-federal-prisoners-in-state-facilities",tabindex:"-1"},[t("§ 37.2-828 Receiving and maintaining federal prisoners in state facilities. "),e("a",{class:"header-anchor",href:"#§-37-2-828-receiving-and-maintaining-federal-prisoners-in-state-facilities","aria-label":'Permalink to "§ 37.2-828 Receiving and maintaining federal prisoners in state facilities."'},"​")],-1),s=e("p",null,"The Commissioner is authorized to enter into a contract with the United States, through the Director of the United States Bureau of Prisons or other authorized agent of the United States, for the reception, maintenance, care, and observation in state facilities, or in those designated by the Commissioner for the purpose, of any persons charged with a crime in the courts of the United States sitting in Virginia and committed by the courts to the state facilities for those purposes. All persons so admitted shall remain subject to the jurisdiction of the court by whom they were committed, and they may be returned to that court at any time for hearing or trial.",-1),h=e("p",null,"Any such contract shall require that the United States remit to the State Treasurer for each prisoner admitted specified per diem or other payments, or both, with such payments fixed by the contract.",-1),c=e("p",null,"The director of any state facility to which a prisoner of the United States is admitted shall observe the person and, as soon as possible, report in writing to the court by which he is certified or committed as to his mental condition or other matters as the court may direct.",-1),d=e("p",null,"No contract made pursuant to this section shall obligate the Commonwealth or the Commissioner to receive a federal prisoner into any state facility in which all available beds are needed for persons otherwise admitted, or in any other case where, in the opinion of the director, the admission of the prisoner would interfere with the care and treatment of other persons admitted or with the proper administration of the state facility.",-1),l=e("p",null,[t("Code 1950, § 37-98; 1950, p. 913; 1968, c. 477, § 37.1-95; 1972, c. 639; 1980, c. 582; 2005, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716",target:"_blank",rel:"noreferrer"},"716"),t(".")],-1),m=[n,s,h,c,d,l];function f(p,u,_,g,y,b){return a(),o("div",null,m)}const S=i(r,[["render",f]]);export{w as __pageData,S as default};
