import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 37.2-835 Arrest without warrant.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-835.md","filePath":"vacode/37.2-835.md"}'),o={name:"vacode/37.2-835.md"},n=r('<h1 id="§-37-2-835-arrest-without-warrant" tabindex="-1">§ 37.2-835 Arrest without warrant. <a class="header-anchor" href="#§-37-2-835-arrest-without-warrant" aria-label="Permalink to &quot;§ 37.2-835 Arrest without warrant.&quot;">​</a></h1><p>Any officer authorized to make arrests is authorized to make an arrest under a warrant issued under the provisions of § <a href="http://law.lis.virginia.gov/vacode/37.2-833/" target="_blank" rel="noreferrer">37.2-833</a> or <a href="http://law.lis.virginia.gov/vacode/37.2-834/" target="_blank" rel="noreferrer">37.2-834</a>, without having the warrant in his possession, provided the warrant has been issued and the arresting officer has been advised of the issuance of the warrant by printed message or any form of wire or wireless communication containing the name of the person wanted, directing the disposition to be made of the person when apprehended, and stating the basis of the issuance of the warrant.</p><p>Code 1950, § 37-97.1; 1954, c. 668; 1968, c. 477, § 37.1-77; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>.</p>',3),i=[n];function s(h,d,c,_,f,p){return a(),t("div",null,i)}const w=e(o,[["render",s]]);export{u as __pageData,w as default};
