import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 37.2-839 Exchange of information between community services boards or behavioral health authorities and state facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-839.md","filePath":"vacode/37.2-839.md"}'),r={name:"vacode/37.2-839.md"},o=t('<h1 id="§-37-2-839-exchange-of-information-between-community-services-boards-or-behavioral-health-authorities-and-state-facilities" tabindex="-1">§ 37.2-839 Exchange of information between community services boards or behavioral health authorities and state facilities. <a class="header-anchor" href="#§-37-2-839-exchange-of-information-between-community-services-boards-or-behavioral-health-authorities-and-state-facilities" aria-label="Permalink to &quot;§ 37.2-839 Exchange of information between community services boards or behavioral health authorities and state facilities.&quot;">​</a></h1><p>Community services boards or behavioral health authorities and state facilities may, when the individual has refused authorization, exchange the information required to prepare and implement a comprehensive individualized treatment plan, including a discharge plan as specified in subsection A of § <a href="http://law.lis.virginia.gov/vacode/37.2-837/" target="_blank" rel="noreferrer">37.2-837</a>. This section shall apply to all individuals receiving services from community services boards, behavioral health authorities, and state facilities.</p><p>When an individual who is deemed suitable for discharge pursuant to subsection A of § <a href="http://law.lis.virginia.gov/vacode/37.2-837/" target="_blank" rel="noreferrer">37.2-837</a> or his guardian or conservator refuses to authorize the release of information that is required to formulate and implement a discharge plan as specified in subsection A of § <a href="http://law.lis.virginia.gov/vacode/37.2-837/" target="_blank" rel="noreferrer">37.2-837</a>, then the community services board or behavioral health authority may release without authorization to those service providers and human service agencies identified in the discharge plan only the information needed to secure those services specified in the plan.</p><p>The release of any other information about an individual receiving services to any agency or person not affiliated directly or by contract with community services boards, behavioral health authorities, or state facilities shall be subject to all regulations adopted by the Board or by agencies of the United States government that govern confidentiality of patient information.</p><p>1985, c. 87, § 37.1-98.2; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',5),n=[o];function s(l,h,c,d,f,v){return a(),i("div",null,n)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
