import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 37.2-845 Procedure when person confined in facility or other institution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-845.md","filePath":"vacode/37.2-845.md"}'),n={name:"vacode/37.2-845.md"},o=r('<h1 id="§-37-2-845-procedure-when-person-confined-in-facility-or-other-institution" tabindex="-1">§ 37.2-845 Procedure when person confined in facility or other institution. <a class="header-anchor" href="#§-37-2-845-procedure-when-person-confined-in-facility-or-other-institution" aria-label="Permalink to &quot;§ 37.2-845 Procedure when person confined in facility or other institution.&quot;">​</a></h1><p>A. If the person referenced in § <a href="http://law.lis.virginia.gov/vacode/37.2-844/" target="_blank" rel="noreferrer">37.2-844</a> is held in custody and actually confined in any facility or other institution, he may file his petition in the circuit court of the county or the city in which the facility or other institution is located or in the circuit court of the county or the city adjoining the county or city in which the facility or other institution is located.</p><p>B. Any proceeding to challenge the continued secure inpatient treatment of any person held in custody as a sexually violent predator under Chapter 9 (§ <a href="http://law.lis.virginia.gov/vacode/37.2-900/" target="_blank" rel="noreferrer">37.2-900</a> et seq.) of this title shall be conducted in the circuit court wherein the person was last convicted of a sexually violent offense or wherein the defendant was deemed unrestorably incompetent and referred for commitment pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-169.3/" target="_blank" rel="noreferrer">19.2-169.3</a>.</p><p>Code 1950, § 37-123; 1950, p. 916; 1968, c. 477, § 37.1-104; 1976, c. 671; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0989" target="_blank" rel="noreferrer">989</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1018" target="_blank" rel="noreferrer">1018</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>.</p>',4),a=[o];function c(l,h,s,d,f,p){return t(),i("div",null,a)}const g=e(n,[["render",c]]);export{_ as __pageData,g as default};
