import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 37.2-905 Review of prisoners convicted of a sexually violent offense; review of unrestorably incompetent defendants charged with sexually violent offenses; petition for commitment; notice to Department of Corrections or referring court regarding disposition of review.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-905.md","filePath":"vacode/37.2-905.md"}'),o={name:"vacode/37.2-905.md"},n=i('<h1 id="§-37-2-905-review-of-prisoners-convicted-of-a-sexually-violent-offense-review-of-unrestorably-incompetent-defendants-charged-with-sexually-violent-offenses-petition-for-commitment-notice-to-department-of-corrections-or-referring-court-regarding-disposition-of-review" tabindex="-1">§ 37.2-905 Review of prisoners convicted of a sexually violent offense; review of unrestorably incompetent defendants charged with sexually violent offenses; petition for commitment; notice to Department of Corrections or referring court regarding disposition of review. <a class="header-anchor" href="#§-37-2-905-review-of-prisoners-convicted-of-a-sexually-violent-offense-review-of-unrestorably-incompetent-defendants-charged-with-sexually-violent-offenses-petition-for-commitment-notice-to-department-of-corrections-or-referring-court-regarding-disposition-of-review" aria-label="Permalink to &quot;§ 37.2-905 Review of prisoners convicted of a sexually violent offense; review of unrestorably incompetent defendants charged with sexually violent offenses; petition for commitment; notice to Department of Corrections or referring court regarding disposition of review.&quot;">​</a></h1><p>A. Upon receipt of a recommendation by the CRC regarding an eligible prisoner or an unrestorably incompetent defendant for review pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-169.3/" target="_blank" rel="noreferrer">19.2-169.3</a>, the Attorney General shall have 90 days to conduct a review of the prisoner or defendant and (i) file a petition for the civil commitment of the prisoner or defendant as a sexually violent predator and stating sufficient facts to support such allegation or (ii) notify the Director and Commissioner, in the case of a prisoner, or the referring court and the Commissioner, in the case of an unrestorably incompetent defendant, that he will not file a petition for commitment. Petitions for commitment shall be filed in the circuit court for the judicial circuit or district in which the prisoner was last convicted of a sexually violent offense or in the circuit court for the judicial circuit or district in which the defendant was deemed unrestorably incompetent and referred for commitment review pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-169.3/" target="_blank" rel="noreferrer">19.2-169.3</a>.</p><p>B. If the Attorney General decides not to file a petition for the civil commitment of a prisoner or defendant, or if a petition is filed but is dismissed for any reason, the Attorney General and the Director may share any relevant information with the probation and parole officer who is to supervise the prisoner and with the Department to the extent allowed by state and federal law.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0946" target="_blank" rel="noreferrer">946</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0985" target="_blank" rel="noreferrer">985</a>, § 37.1-70.6; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0989" target="_blank" rel="noreferrer">989</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1018" target="_blank" rel="noreferrer">1018</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0876" target="_blank" rel="noreferrer">876</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>.</p>',4),a=[n];function l(f,s,c,d,g,p){return r(),t("div",null,a)}const m=e(o,[["render",l]]);export{v as __pageData,m as default};
