import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 37.2-920 Appeal by Attorney General; emergency custody order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/37.2-920.md","filePath":"vacode/37.2-920.md"}'),o={name:"vacode/37.2-920.md"},n=t('<h1 id="§-37-2-920-appeal-by-attorney-general-emergency-custody-order" tabindex="-1">§ 37.2-920 Appeal by Attorney General; emergency custody order. <a class="header-anchor" href="#§-37-2-920-appeal-by-attorney-general-emergency-custody-order" aria-label="Permalink to &quot;§ 37.2-920 Appeal by Attorney General; emergency custody order.&quot;">​</a></h1><p>In any case in which the Attorney General successfully appeals the trial court&#39;s denial of probable cause, denial of civil commitment or conditional release, or discharge or placement on conditional release after an annual review hearing, upon the issuance of the mandate by the Court of Appeals, the trial court shall immediately issue an emergency custody order to any local law-enforcement official to have the person taken into custody and held in the local correctional facility, pending further appropriate proceedings.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',3),l=[n];function c(i,s,p,d,h,_){return a(),r("div",null,l)}const g=e(o,[["render",c]]);export{f as __pageData,g as default};
