import{_ as a,o as i,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-1034 How domestic mutual insurers may acquire initial surplus.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1034.md","filePath":"vacode/38.2-1034.md"}'),s={name:"vacode/38.2-1034.md"},n=e("h1",{id:"§-38-2-1034-how-domestic-mutual-insurers-may-acquire-initial-surplus",tabindex:"-1"},[t("§ 38.2-1034 How domestic mutual insurers may acquire initial surplus. "),e("a",{class:"header-anchor",href:"#§-38-2-1034-how-domestic-mutual-insurers-may-acquire-initial-surplus","aria-label":'Permalink to "§ 38.2-1034 How domestic mutual insurers may acquire initial surplus."'},"​")],-1),o=e("p",null,"Any domestic mutual insurer or mutual assessment property and casualty insurer may, without pledging any of its assets, provide a guaranty fund sufficient to defray the expenses of its organization and its initial minimum surplus required to obtain a license to do the business of insurance. The fund may be increased with the prior approval of the Commission by receiving advances or by borrowing funds upon an agreement that the funds, including interest at a rate not exceeding the one-year treasury bill interest rate plus three percentage points at the time the loan is made or renewed, shall be repaid only if the insurer has sufficient earned surplus. The agreement shall provide that the insurer may repay the advances or loans or any part of them whenever it is able to do so in accordance with the requirements of this article. No commission or brokerage shall be paid in acquiring the funds. No repayments of principal, either in whole or in part, and no payments of interest, shall be made without the prior written approval of the Commission. Neither the principal advanced or borrowed nor any interest accrued thereon under this provision shall form a part of the legal liabilities of the insurer until the Commission approves the repayment of such principal or the payment of interest thereon. However, all statements published or filed by the insurer shall show accrued interest and the amount of principal remaining unpaid. All claims under the instrument shall be subordinated to policyholder, claimant and beneficiary claims as well as debts owed to all other classes of creditors.",-1),l=e("p",null,[t("Code 1950, § 38-512; 1952, c. 317, § 38.1-92; 1960, c. 291, § 38.1-92.1; 1970, c. 595; 1980, c. 187; 1986, c. 562; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0503",target:"_blank",rel:"noreferrer"},"503"),t(".")],-1),u=[n,o,l];function c(d,h,m,p,f,y){return i(),r("div",null,u)}const g=a(s,[["render",c]]);export{_ as __pageData,g as default};
