import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-106 Annuities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-106.md","filePath":"vacode/38.2-106.md"}'),n={name:"vacode/38.2-106.md"},r=i('<h1 id="§-38-2-106-annuities" tabindex="-1">§ 38.2-106 Annuities. <a class="header-anchor" href="#§-38-2-106-annuities" aria-label="Permalink to &quot;§ 38.2-106 Annuities.&quot;">​</a></h1><p>&quot;Annuities&quot; means all agreements to make periodic payments in specified or calculable sums pursuant to the terms of a contract for a stated period of time or for the life of the person or persons specified in the contract. &quot;Annuities&quot; does not include contracts defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-102/" target="_blank" rel="noreferrer">38.2-102</a> and qualified charitable gift annuities as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-106.1/" target="_blank" rel="noreferrer">38.2-106.1</a>.</p><p>As used in this title, unless the context requires otherwise, &quot;annuity&quot; shall be deemed to include &quot;variable annuity&quot; and &quot;modified guaranteed annuity,&quot; and shall be deemed to include a contract under which a lump sum cash settlement is an alternative to the option of periodic payments.</p><p>1952, c. 317, § 38.1-4; 1966, c. 289; 1970, c. 532; 1985, c. 312; 1986, c. 562; 1992, c. 210; 1993, c. 764; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0425" target="_blank" rel="noreferrer">425</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>.</p>',4),o=[r];function s(c,l,d,u,p,f){return t(),a("div",null,o)}const m=e(n,[["render",s]]);export{_ as __pageData,m as default};
