import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-113 Burglary and theft.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-113.md","filePath":"vacode/38.2-113.md"}'),o={name:"vacode/38.2-113.md"},n=t('<h1 id="§-38-2-113-burglary-and-theft" tabindex="-1">§ 38.2-113 Burglary and theft. <a class="header-anchor" href="#§-38-2-113-burglary-and-theft" aria-label="Permalink to &quot;§ 38.2-113 Burglary and theft.&quot;">​</a></h1><p>&quot;Burglary and theft insurance&quot; means insurance against:</p><ol><li><p>Loss of or damage to any property resulting from actual or attempted burglary, theft, larceny, robbery, forgery, fraud, vandalism, malicious mischief, wrongful confiscation or wrongful conversion, disposal or concealment by any person or persons;</p></li><li><p>Loss of or damage to moneys, coins, bullion, securities, notes, drafts, acceptances or any other valuable papers or documents, resulting from any cause, except while in the custody or possession of and being transported by any carrier for hire or in the mail; or</p></li><li><p>The loss of property actually surrendered due to extortion, threat, or demand, involving the actual, alleged or threatened kidnapping of any individual or the threat to do bodily injury to or damage to property of or to wrongfully abduct or detain any individual.</p></li></ol><p>Any policy of burglary and theft insurance may include appropriate provisions obligating the insurer to pay medical, hospital, surgical, and funeral expenses arising out of the death, dismemberment, sickness, or injury of any person, and death and dismemberment benefits in the event of death or dismemberment, if the death, dismemberment, sickness, or injury is caused by or is incidental to a cause of loss insured under the policy.</p><p>1952, c. 317, § 38.1-9; 1986, c. 562; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>.</p>',5),i=[n];function s(l,d,c,u,p,f){return a(),r("div",null,i)}const y=e(o,[["render",s]]);export{m as __pageData,y as default};
